import React from 'react';
import { AuthOptions, FinishedData } from '../../../auth';
import { UserSession } from 'blockstack/lib';
declare enum States {
    MODAL_OPEN = "modal/open",
    MODAL_CLOSE = "modal/close",
    START_AUTH = "data/START_AUTH",
    FINISH_AUTH = "data/FINISH_AUTH",
    CANCEL_AUTH = "data/CANCEL_AUTH",
    UPDATE_AUTH_OPTIONS = "data/update-auth-options",
    SCREENS_INTRO = "screens/intro",
    SCREENS_HOW_IT_WORKS = "screens/how-it-works",
    SCREENS_SIGN_IN = "screens/sign-in"
}
declare type Action = {
    type: string;
    payload?: any;
};
declare type Dispatch = (action: Action) => void;
declare type State = {
    isOpen: boolean;
    isAuthenticating: boolean;
    screen: string;
    authData?: FinishedData;
    authOptions: AuthOptions;
    userSession?: UserSession;
};
declare const initialState: State;
declare const connectReducer: (state: State, { type, payload }: {
    type: string;
    payload?: any;
}) => {
    isAuthenticating: boolean;
    authData: any;
    isOpen: boolean;
    screen: string;
    authOptions: AuthOptions;
    userSession?: UserSession | undefined;
} | {
    authOptions: any;
    isOpen: boolean;
    isAuthenticating: boolean;
    screen: string;
    authData?: FinishedData | undefined;
    userSession?: UserSession | undefined;
};
declare const ConnectContext: React.Context<State>;
declare const ConnectDispatchContext: React.Context<Dispatch | undefined>;
declare const ConnectProvider: ({ authOptions, children, }: {
    authOptions: AuthOptions;
    children: any;
}) => JSX.Element;
export { initialState, connectReducer, ConnectContext, ConnectDispatchContext, ConnectProvider, States, };
